jQuery(document).ready(function() {

			jQuery(".form-avaliacao .form-avaliacao-parte-1 input").keyup(function(){
				
				
				jQuery(this).removeClass("certo");
				
				jQuery(this).removeClass("errado");
				
			});
																	   
			jQuery(".form-avaliacao .form-avaliacao-parte-1 select").on("click", function() {
																	   
																	   
				jQuery(this).removeClass("certo");
				
				jQuery(this).removeClass("errado");

			});
	
												   
			jQuery("#parte-2").on("click", function() {
				
				empty = false;

				jQuery(".form-avaliacao .form-avaliacao-parte-1 select").addClass("certo");

				jQuery('.form-avaliacao .form-avaliacao-parte-1 input').each(function() {

					if (jQuery(this).val() == '' && jQuery(this).attr('id') != "celular") {


						jQuery(this).removeClass("certo");


						jQuery(this).addClass("errado");

						empty = true;


					} else if (jQuery(this).attr('id') == "celular") {
						

						if (jQuery(this).val().replace(/[^\d]/g, "").length != 11) {

							jQuery(this).removeClass("certo");

							jQuery(this).addClass("errado");

							empty = true;

						}else{
							
							jQuery(this).removeClass("errado");

							jQuery(this).addClass("certo");
							
						}

					} else {

						jQuery(this).removeClass("errado");

						jQuery(this).addClass("certo");


					}
				});


				if (empty) {
					
					jQuery('html, body').animate({
						 scrollTop: jQuery("#avaliacao").offset().top
					 }, 1500);

				} else {
					
					jQuery('html, body').animate({
						 scrollTop: jQuery("#avaliacao").offset().top
					 }, 1500);

					jQuery(".form-avaliacao .form-avaliacao-parte-1 input").removeClass("errado");

					jQuery(".form-avaliacao .form-avaliacao-parte-1 input").addClass("certo");
					
					jQuery("span.parte-01").addClass("correto");
					
					jQuery("span.parte-02").addClass("ativo");
					
					jQuery("#chamada h2").text("Nos informe qual o seu estado atual e histórico de calvície, para que possamos avaliar ainda melhor seu caso:");

					jQuery(".form-avaliacao-parte-1").removeClass("ativo");
					setTimeout(function() {
						jQuery(".form-avaliacao-parte-2").addClass("ativo");
					}, 510);

				}

			});
	
	});