jQuery(document).ready(function() {

	jQuery("#parte-1").on("click", function() {
		jQuery("span.parte-01").removeClass("correto");		
		jQuery("span.parte-02").removeClass("ativo");
		jQuery(".form-avaliacao-parte-2").removeClass("ativo");
		jQuery("#chamada h2").html("Para começar sua avaliação Online,<br/>nos deixe saber um pouco mais sobre você:");
		setTimeout(function() {
			jQuery(".form-avaliacao-parte-1").addClass("ativo");
			jQuery('html, body').animate({
						 scrollTop: jQuery("#avaliacao").offset().top
					 }, 1500);
		}, 510);


	});
	
	jQuery(".form-avaliacao .form-avaliacao-parte-2 select").on("click", function() {
																	   
																	   
				jQuery(this).removeClass("certo");
				
				jQuery(this).removeClass("errado");

			});


	jQuery("#enviar-informacoes").on("click", function() {
		
		check = false;

		jQuery(".form-avaliacao .form-avaliacao-parte-2 select").addClass("certo");
		
		jQuery('span.wpcf7-form-control-wrap  #tratamento-feito  span.wpcf7-list-item label input[type=checkbox]').each(function(){
        
        if(jQuery( this ).prop("checked")){

			check = true;


        }

    });
		
	if(check){
		
		jQuery("span.wpcf7-form-control-wrap  #tratamento-feito  span.wpcf7-list-item").removeClass("errado");

		jQuery("span.wpcf7-form-control-wrap  #tratamento-feito  span.wpcf7-list-item").addClass("certo");
	   
	   }else{
		   
		   jQuery('html, body').animate({
						 scrollTop: jQuery("#avaliacao").offset().top
					 }, 1500);
		   
		   jQuery("span.wpcf7-form-control-wrap  #tratamento-feito  span.wpcf7-list-item").removeClass("certo");

			jQuery("span.wpcf7-form-control-wrap  #tratamento-feito  span.wpcf7-list-item").addClass("errado");
		   
	   }

	});
	
});

